import { createSlice } from "@reduxjs/toolkit";

const user = createSlice({
    name: "user", //state 이름
    initialState: {
        loginUser:null,
    },
    reducers:{
        loginUser: (state, action) => {
            state.loginUser = action.payload;
        },
    },
});

export const { loginUser } = user.actions;
export default user;
