import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import * as CF from "../../../config/function";
import { enum_api_uri } from "../../../config/enum";
import { notiPop, confirmPop } from "../../../store/popupSlice";
import ConfirmPop from "../../popup/ConfirmPop";

const NotiPop = (props) => {
    const dispatch = useDispatch();
    const popup = useSelector((state)=>state.popup);
    const user = useSelector((state)=>state.user);
    const alarm_list = enum_api_uri.alarm_list;
    const [confirm, setConfirm] = useState(false);
    const [tab, setTab] = useState("");
    const [total, setTotal] = useState(0);
    const [list, setList] = useState([]);


    // Confirm팝업 닫힐때
    useEffect(()=>{
        if(popup.confirmPop === false){
            setConfirm(false);
        }
    },[popup.confirmPop]);


    //팝업닫기
    const closePopHandler = () => {
        dispatch(notiPop(false));
    };


    //알림 가져오기
    const getAlarmList = () => {
        axios.get(`${alarm_list.replace(":follow",tab)}`,
            {headers:{Authorization: `Bearer ${user.loginUser.accessToken}`}}
        )
        .then((res)=>{
            if(res.status === 200){
                let data = res.data.data;
                setTotal(data.totalCnt);
                setList(data.list);
            }
        })
        .catch((error) => {
            const err_msg = CF.errorMsgHandler(error);
            dispatch(confirmPop({
                confirmPop:true,
                confirmPopTit:'알림',
                confirmPopTxt: err_msg,
                confirmPopBtn:1,
            }));
            setConfirm(true);
        });
    };


    useEffect(()=>{
        getAlarmList();
    },[]);


    return(<>
        <div className="pop_noti">
            <h5>알림</h5>
            <div className="pop_inner">
                <ul className="tab_noti">
                    <li className="on"><button type="button">전체</button></li>
                    <li><button type="button">게시글</button></li>
                    <li><button type="button">댓글</button></li>
                </ul>
                <div className="noti_box">
                    <div className="noti_util">
                        <span>알림이 총 <b>{CF.MakeIntComma(total)}</b>개가 있습니다.</span>                                                
                        <ul className="btn_wrap">
                            <li>
                                <button type="button">전체 읽기</button>
                            </li>
                            <li>
                                <button type="button">읽은 알림 삭제</button>
                            </li>
                        </ul>
                    </div>
                    {list.length > 0 ?
                        <ul className="list_noti">
                            {list.map((cont,i)=>{
                                return(
                                    <li className="read">
                                        <div className="cate">게시글</div>
                                        <div className="txt_wrap">
                                            <a href="#" rel="noopener noreferrer">
                                                <em>[공지사항] ‘8월 9일(수) 확인된 현상 안내’ 댓글 작성</em>
                                                <strong>
                                                    <b>이선영</b>
                                                    <span>알려 주셔서 감사합니다!</span>
                                                </strong>
                                                <i>2023.07.23</i>
                                            </a>
                                        </div>
                                        <button type="button" className="btn_noti_remove">알림 삭제</button>
                                    </li>
                                );
                            })}
                        </ul>
                        : <div></div>
                    }
                </div>
            </div>
            <button type="button" className="btn_noti_close" onClick={closePopHandler}>알림팝업 닫기</button>
        </div>

        {/* confirm팝업 */}
        {confirm && <ConfirmPop />}
    </>);
};

export default NotiPop;
