import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { enum_api_uri } from "../../config/enum";
import * as CF from "../../config/function";
import { confirmPop } from "../../store/popupSlice";
import InputBox from "../../components/component/admin/InputBox";
import ConfirmPop from "../../components/popup/ConfirmPop";


const SettingSiteInfo = () => {
    const site_info = enum_api_uri.site_info;
    const site_info_modify = enum_api_uri.site_info_modify;
    const dispatch = useDispatch();
    const popup = useSelector((state)=>state.popup);
    const user = useSelector((state)=>state.user);
    const [confirm, setConfirm] = useState(false);
    const [info, setInfo] = useState({});


    // Confirm팝업 닫힐때
    useEffect(()=>{
        if(popup.confirmPop === false){
            setConfirm(false);
        }
    },[popup.confirmPop]);


    //사이트정보 가져오기
    const getInfo = () => {
        axios.get(`${site_info.replace(":site_id",user.siteId)}`,
            {headers:{Authorization: `Bearer ${user.token}`}}
        )
        .then((res)=>{
            if(res.status === 200){
                let data = res.data.data;
                    data.site_id = user.siteId;
                setInfo(data);
            }
        })
        .catch((error) => {
            const err_msg = CF.errorMsgHandler(error);
            dispatch(confirmPop({
                confirmPop:true,
                confirmPopTit:'알림',
                confirmPopTxt: err_msg,
                confirmPopBtn:1,
            }));
            setConfirm(true);
        });
    };


    //맨처음 사이트정보 가져오기
    useEffect(()=>{
        getInfo();
    },[]);


    useEffect(()=>{
        if(Object.keys(info).length > 0){
            setInfo(info);
        }
    },[info]);


    //인풋값 변경시
    const onInputChangeHandler = (e) => {
        const id = e.currentTarget.id;
        const val = e.currentTarget.value;

        let newInfo = {...info};
            newInfo[id] = val;

        setInfo(newInfo);
    };


    //저장버튼 클릭시
    const saveBtnClickHandler = () => {
        if(!info.c_site_name){
            dispatch(confirmPop({
                confirmPop:true,
                confirmPopTit:'알림',
                confirmPopTxt:'사이트이름을 입력해주세요.',
                confirmPopBtn:1,
            }));
            setConfirm(true);
        }else if(!info.c_b_title){
            dispatch(confirmPop({
                confirmPop:true,
                confirmPopTit:'알림',
                confirmPopTxt:'브라우저 타이틀을 입력해주세요.',
                confirmPopBtn:1,
            }));
            setConfirm(true);
        }else{
            saveHandler();
        }
    };


    //사이트정보 저장하기
    const saveHandler = () => {
        const body = info;
        axios.put(`${site_info_modify}`, body, 
            {headers:{Authorization: `Bearer ${user.token}`}}
        )
        .then((res)=>{
            if(res.status === 200){
                dispatch(confirmPop({
                    confirmPop:true,
                    confirmPopTit:'알림',
                    confirmPopTxt:'',
                    confirmPopBtn:1,
                }));
                setConfirm(true);
            }
        })
        .catch((error) => {
            const err_msg = CF.errorMsgHandler(error);
            dispatch(confirmPop({
                confirmPop:true,
                confirmPopTit:'알림',
                confirmPopTxt: err_msg,
                confirmPopBtn:1,
            }));
            setConfirm(true);
        });
    };


    return(<>
        <div className="page_admin_setting">
            <div className="content_box">
                <div className="tit">
                    <h3>
                        <b>기본 정보</b>
                    </h3>
                    <p>입력된 정보는 웹사이트 하단과 개인정보취급방침 고지란에 기재됩니다.</p>
                </div>
                <div className="tbl_wrap2">
                    <table>
                        <caption>정보 테이블</caption>
                        <colgroup>
                            <col style={{width: "140px"}}/>
                            <col style={{width: "auto"}}/>
                            <col style={{width: "24px"}}/>
                            <col style={{width: "140px"}}/>
                            <col style={{width: "auto"}}/>
                        </colgroup>
                        <tbody>
                            <tr>
                                <th>사이트이름 <i>*</i></th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`사이트이름을 입력해주세요.`}
                                        countShow={true}
                                        countMax={16}
                                        count={info.c_site_name ? info.c_site_name.length : 0}
                                        value={info.c_site_name || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_site_name`}
                                    />
                                </td>
                                <td></td>
                                <th>웹 브라우저 타이틀</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`웹 브라우저 타이틀을 입력해주세요.`}
                                        countShow={true}
                                        countMax={16}
                                        count={info.c_web_title ? info.c_web_title.length : 0}
                                        value={info.c_web_title || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_web_title`}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th>대표자</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`대표자를 입력해주세요.`}
                                        value={info.c_ceo || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_ceo`}
                                    />
                                </td>
                                <td></td>
                                <th>대표전화</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`대표전화를 입력해주세요.`}
                                        value={info.c_tel || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_tel`}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th>사업자번호</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`사업자번호를 입력해주세요.`}
                                        value={info.c_num || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_num`}
                                    />
                                </td>
                                <td></td>
                                <th>통신판매번호</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`통신판매번호를 입력해주세요.`}
                                        value={info.c_num2 || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_num2`}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th>이메일</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`이메일을 입력해주세요.`}
                                        value={info.c_email || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_email`}
                                    />
                                </td>
                                <td></td>
                                <th>주소</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`주소를 입력해주세요.`}
                                        value={info.c_address || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_address`}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th>FAX 번호</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`FAX 번호를 입력해주세요.`}
                                        value={info.c_fax || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_fax`}
                                    />
                                </td>
                                <td></td>
                                <th>개인정보관리책임자</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`개인정보관리책임자를 입력해주세요.`}
                                        value={info.c_manager || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_manager`}
                                    />
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
            <div className="content_box">
                <div className="tit">
                    <h3>
                        <b>메타정보</b>
                    </h3>
                    <p>웹마스터 도구에 활용되어 포털 검색 시 노출되는 정보입니다.</p>
                </div>
                <div className="tbl_wrap2">
                    <table>
                        <caption>정보 테이블</caption>
                        <colgroup>
                            <col style={{width: "140px"}}/>
                            <col style={{width: "auto"}}/>
                            <col style={{width: "24px"}}/>
                            <col style={{width: "140px"}}/>
                            <col style={{width: "auto"}}/>
                        </colgroup>
                        <tbody>
                            <tr>
                                <th>브라우저 타이틀 <i>*</i></th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`브라우저 타이틀을 입력해주세요.`}
                                        value={info.c_b_title || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_b_title`}
                                    />
                                </td>
                                <td></td>
                                <th>메타설명</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`메타설명을 입력해주세요.`}
                                        value={info.c_meta || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_meta`}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th>메타 태그</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`메타 태그를 입력해주세요.`}
                                        value={info.c_meta_tag || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_meta_tag`}
                                    />
                                </td>
                                <td></td>
                                <th>메타 분류</th>
                                <td>
                                    <InputBox 
                                        type={`text`}
                                        placeholder={`메타 분류를 입력해주세요.`}
                                        value={info.c_meta_type || ""}
                                        onChangeHandler={onInputChangeHandler}
                                        id={`c_meta_type`}
                                    />
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
            <div className="form_btn_wrap">
                <button type="button" className="btn_type3">취소</button>
                <button type="button" className="btn_type4" onClick={saveBtnClickHandler}>저장</button>
            </div>
        </div>

        {/* confirm팝업 */}
        {confirm && <ConfirmPop />}
    </>);
};

export default SettingSiteInfo;
