import { createSlice } from "@reduxjs/toolkit";

const user = createSlice({
    name: "user", //state 이름
    initialState: {
        token: "",
        siteId: "",
    },
    reducers:{
        token: (state, action) => {
            state.token = action.payload;
        },
        siteId: (state, action) => {
            state.siteId = action.payload;
        },
    },
});

export const { token, siteId } = user.actions;
export default user;
