import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { enum_api_uri } from "../../config/enum";
import * as CF from "../../config/function";
import { confirmPop } from "../../store/popupSlice";
import InputBox from "../../components/component/admin/InputBox";
import ConfirmPop from "../../components/popup/ConfirmPop";

const Login = () => {
    const site_info = enum_api_uri.site_info;
    const dispatch = useDispatch();
    const popup = useSelector((state)=>state.popup);
    const [confirm, setConfirm] = useState(false);
    const [info, setInfo] = useState({});


    // Confirm팝업 닫힐때
    useEffect(()=>{
        if(popup.confirmPop === false){
            setConfirm(false);
        }
    },[popup.confirmPop]);


    //사이트정보 가져오기
    const getInfo = () => {
        axios.get(`${site_info.replace(":site_id","likeweb")}`)
        .then((res)=>{
            if(res.status === 200){
                let data = res.data.data;
                setInfo(data);
            }
        })
        .catch((error) => {
            const err_msg = CF.errorMsgHandler(error);
            dispatch(confirmPop({
                confirmPop:true,
                confirmPopTit:'알림',
                confirmPopTxt: err_msg,
                confirmPopBtn:1,
            }));
            setConfirm(true);
        });
    };


    //맨처음 사이트정보 가져오기
    useEffect(()=>{
        getInfo();
    },[]);

    return(<>
        <main id="main" className="main">
            <div className="page_admin_login">
                <div className="admin_login">
                    <div className="login_tit">
                        <h1 className="logo">Lorem ipsum</h1>
                        <strong>로그인</strong>
                    </div>
                    <div className="form_inner">
                        <div className="form_input">
                            <div className="input_wrap">
                                <div className="input_box">
                                    {/* input 조건 미 충족 시 class 추가 wrong_input */}
                                    <input type="text" placeholder="이메일" className="wrong_input" />
                                </div>
                                <em className="txt_err">이메일 입력 조건이 맞지 않습니다.</em>
                            </div>
                        </div>
                        <div className="form_input">
                            <div className="input_wrap">
                                <div className="input_box pwd_input">
                                    <input type="password" placeholder="비밀번호" />
                                    <button type="button" className="view_pwd">비밀번호 보기</button>
                                </div>
                                <em className="txt_err">비밀번호 입력 조건이 맞지 않습니다.</em>
                            </div>
                        </div>
                        <div className="btn_wrap">
                            <a href="/" rel="noopener noreferrer" className="btn_type25">로그인</a>
                            <a href="/" rel="noopener noreferrer" className="btn_type26">사용자 화면 바로가기</a>
                        </div>
                    </div>
                </div>
            </div>
        </main>

        {/* confirm팝업 */}
        {confirm && <ConfirmPop />}
    </>);
};

export default Login;
