import { createSlice } from "@reduxjs/toolkit";

const etc = createSlice({
    name: "etc", //state 이름
    initialState: {
        notiPop: false,
    },
    reducers:{
        notiPop: (state, action) => {
            state.notiPop = action.payload;
        },
    }
});

export const {
    confirmPop, 
} = etc.actions;
export default etc;
